import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

function BackgroundGrid() {
  const broadData = [0, 0, 1, 0];
  const size = Math.sqrt(broadData.length);

  const rows = [];

  for (let i = 0; i < broadData.length; i += size) {
    let columns = [];
    for (let j = 0; j < size; j++) {}

    let row = <div className="grid-row">{columns}</div>;
    rows.push();
  }
  return (
    <div className="background-grid">
      <div className="grid-row">
        <div className="grid-cell">{broadData[0] <= 0 ? "" : broadData[0]}</div>
        <div className="grid-cell">{broadData[1] <= 0 ? "" : broadData[1]}</div>
      </div>
      <div className="grid-row">
        <div className="grid-cell">{broadData[2] <= 0 ? "" : broadData[2]}</div>
        <div className="grid-cell">{broadData[3] <= 0 ? "" : broadData[3]}</div>
      </div>
    </div>
  );
}

ReactDOM.render(<BackgroundGrid />, document.getElementById("root"));
