import { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";
import { SquareComponent } from "./square-component";

function BackgroundGrid() {
  const [broadData, setBroadData] = useState([0, 0, 0, 0, 0, 0, 0, 0, 0]);
  const size = Math.sqrt(broadData.length);
  //const copyBroadData = broadData;
  //const broadData = [0, 0, 0, 0, 0, 0, 0, 0, 0];

  const generateRandomIndex = (rangeValue, excludeValue) => {
    if (rangeValue === excludeValue.length) {
      return;
    }

    const randomIndex = Math.floor(Math.random() * rangeValue);

    // 不匹配数组中不包含随机数
    if (!excludeValue.includes(randomIndex)) {
      return randomIndex;
    }

    return generateRandomIndex(rangeValue, excludeValue);
  };
  const excludeIndex = [];

  // 更新broadData
  useEffect(() => {
    // 生成种子方块的index
    const seedIndex = generateRandomIndex(broadData.length, excludeIndex);

    broadData[seedIndex] = 1;
    excludeIndex.push(seedIndex);

    const newIndex = generateRandomIndex(broadData.length, excludeIndex);
    if (newIndex === undefined) {
      // TODO:gameover
    }
    broadData[newIndex] = 1;
    excludeIndex.push(newIndex);
    setBroadData(broadData.slice());
  }, []);

  // 键盘事件
  document.addEventListener("keyup", function onKeyUp(e) {
    if (e.repeat) {
      return;
    }
    switch (e.keyCode) {
      case 37:
        horizontalMove(e.keyCode);
        break;
      case 38:
        verticalMove(e.keyCode);
        break;
      case 39:
        horizontalMove(e.keyCode);
        break;
      case 40:
        verticalMove(e.keyCode);
        break;
      default:
        break;
    }
  });
  // 垂直移动
  const verticalMove = (code) => {
    for (let i = 0; i < size; i++) {
      const colunmData = [];
      for (let j = 0; j < size; j++) {
        // 得到每一列的数据
        colunmData.push(broadData[size * j + i]);
      }

      let haveValueData = [];
      colunmData.forEach((item, index) => {
        if (item !== 0) {
          haveValueData.push(item);
          // 取出不为0的值后，剩下的全为O
          colunmData[index] = 0;
        }
      });

      if (haveValueData.length > 1) {
        if (code === 38) {
          // 往上相加
          haveValueData = calcAdd(haveValueData);

          // 移动位置
          haveValueData.forEach((item, index) => {
            colunmData[index] = item;
          });
        } else {
          // 往下相加
          haveValueData = calcAdd(haveValueData.reverse());
          haveValueData.reverse();

          // 移动位置
          haveValueData.forEach((item, index) => {
            colunmData[colunmData.length - 1 - index] = item;
          });
        }
      } else if (haveValueData.length === 1) {
        if (code === 38) {
          // 移动到第一位
          colunmData[0] = haveValueData[0];
        } else {
          // 移动到最后一位
          colunmData[size - 1] = haveValueData[0];
        }
      }

      //const arr = [0, 0, 0, 0, 0, 0, 0, 0, 0];
      // 将更改后的数据放入新得board数组中
      colunmData.forEach((item, index) => {
        broadData[size * index + i] = item;
      });
    }
    setBroadData(broadData.slice());
  };

  // 水平移动
  const horizontalMove = (code) => {
    const isStartFromHead = true;
    for (let i = 0; i < size; i++) {
      // 获取每一行
      const rowData = broadData.slice(i * size, (i + 1) * size);

      // 将每行中不为0的值保存在数组中
      let haveValueData = [];
      rowData.forEach((item, index) => {
        if (item !== 0) {
          haveValueData.push(item);
          // 取出不为0的值后，剩下的全为O
          rowData[index] = 0;
        }
      });

      if (haveValueData.length > 1) {
        if (code === 37) {
          // 往左相加
          // 得到相加后的数组
          haveValueData = calcAdd(haveValueData);

          // 移动位置
          haveValueData.forEach((item, index) => {
            rowData[index] = item;
          });

          // 新增方块
        } else {
          // 往右相加

          // 倒序
          haveValueData = calcAdd(haveValueData.reverse());
          haveValueData.reverse();

          // 移动位置
          haveValueData.forEach((item, index) => {
            rowData[rowData.length - 1 - index] = item;
          });

          // 新增方块
        }
      } /* else if (haveValueData.length === 1) {
        if (code === 37) {
          // 移动到第一位
          rowData[0] = haveValueData[0];
        } else {
          // 移动到最后一位
          rowData[size - 1] = haveValueData[0];
        }
      } */

      // 将更改后的数据放入新得board数组中
      rowData.forEach((item, index) => {
        broadData[i * size + index] = item;
      });
    }
    setBroadData(broadData.slice());
  };

  // 加法计算
  const calcAdd = (arr) => {
    const newArr = [];
    while (arr.length >= 1) {
      if (arr.length === 1) {
        newArr.push(arr[0]);
        break;
      }
      // 截取两位
      const calcData = arr.slice(0, 2);

      if (calcData[0] === calcData[1]) {
        newArr.push(calcData[0] + calcData[1]);
        arr.splice(0, 2);
      } else {
        newArr.push(calcData[0]);
        arr.splice(0, 1);
      }
    }
    return newArr;
  };

  // 行数据填充
  const fillRowData = (oldRow, dataArr, isStartFromHead) => {
    // 从头部开始填充
    if (isStartFromHead) {
      dataArr.forEach((item, index) => {
        oldRow[index] = item;
      });
      return oldRow;
    }

    dataArr.forEach((item, index) => {
      oldRow[oldRow.length - 1 - index] = item;
    });
    return oldRow;
  };

  const rows = [];
  // 生成棋盘
  for (let i = 0; i < broadData.length; i += size) {
    let columns = [];
    for (let j = 0; j < size; j++) {
      const column = (
        <div className="grid-cell" key={i + j}>
          {broadData[i + j] <= 0 ? (
            ""
          ) : (
            <SquareComponent value={broadData[i + j]} />
          )}
        </div>
      );
      columns.push(column);
    }

    const row = (
      <div className="grid-row" key={i}>
        {columns}
      </div>
    );
    rows.push(row);
  }
  return <div className="background-grid">{rows}</div>;
}

ReactDOM.render(<BackgroundGrid />, document.getElementById("root"));
