//import React, { useEffect } from "react";
import ReactDOM from "react-dom";
import "./index.css";
import { SquareComponent } from "./square-component";

function BackgroundGrid() {
  const generateRandomIndex = (rangeValue, excludeValue) => {
    if (rangeValue === excludeValue.length) {
      return;
    }

    const randomIndex = Math.floor(Math.random() * rangeValue);

    // 不匹配数组中不包含随机数
    if (!excludeValue.includes(randomIndex)) {
      return randomIndex;
    }

    return generateRandomIndex(rangeValue, excludeValue);
  };

  const broadData = [0, 0, 0, 0, 0, 0, 0, 0, 0];
  const size = Math.sqrt(broadData.length);

  const excludeIndex = [];
  // 生成种子方块的index
  const seedIndex = generateRandomIndex(broadData.length, excludeIndex);

  broadData[seedIndex] = 1;
  excludeIndex.push(seedIndex);

  // 生成新增方块的index
  const newIndex = generateRandomIndex(broadData.length, excludeIndex);
  if (newIndex === undefined) {
    // TODO:gameover
  }
  broadData[newIndex] = 1;
  excludeIndex.push(newIndex);

  const rows = [];

  // 键盘事件
  /*  useEffect(() => {
    // 注册键盘事件
    document.addEventListener("keyup", onkeyup);
    // 清除键盘事件
    return () => {
      document.removeEventListener("keyup", onkeyup);
    };
  }, []); */
  document.addEventListener("keyup", onkeyup);
  onkeyup = (e) => {
    switch (e.keyCode) {
      case 37:
        console.log("left");
        break;
      case 38:
        console.log("up");
        break;
      case 39:
        moveRight();
        break;
      case 40:
        console.log("down");
        break;
      default:
        break;
    }
  };

  // 右移
  const moveRight = () => {
    const newBoardData = [];
    for (let i = 0; i < size; i++) {
      // 获取每一行
      const rowData = broadData.slice(i * size, (i + 1) * size);

      // 将每行中不为0的值保存在数组中
      const haveValueData = [];
      rowData.forEach((item, index) => {
        if (item !== 0) {
          haveValueData.push(item);
          // 取出不为0的值后，剩下的全为O
          rowData[index] = 0;
        }
      });

      if (haveValueData.length > 1) {
        // 相加
      } else {
        // 移动到最后一位
        rowData[size - 1] = haveValueData[0];
      }

      // 将更改后的数据放入新得board数组中
      rowData.forEach((item) => {
        newBoardData.push(item);
      });

      console.log(haveValueData);
    }
  };

  // 生成棋盘
  for (let i = 0; i < broadData.length; i += size) {
    let columns = [];
    for (let j = 0; j < size; j++) {
      const column = (
        <div className="grid-cell" key={i + j}>
          {broadData[i + j] <= 0 ? (
            ""
          ) : (
            <SquareComponent value={broadData[i + j]} />
          )}
        </div>
      );
      columns.push(column);
    }

    const row = (
      <div className="grid-row" key={i}>
        {columns}
      </div>
    );
    rows.push(row);
  }
  return <div className="background-grid">{rows}</div>;
}

ReactDOM.render(<BackgroundGrid />, document.getElementById("root"));
