import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

function BackgroundGrid() {
  const broadData = [0, 0, 1, 0,0, 0, 1, 0,0];
  const size = Math.sqrt(broadData.length);

  const rows = [];

  for (let i = 0; i < broadData.length; i += size) {
    let columns = [];
    for (let j = 0; j < size; j++) {
      const column = (
        <div className="grid-cell">
          {broadData[i + j] <= 0 ? "" : broadData[i + j]}
        </div>
      );
      columns.push(column);
    }

    const row = <div className="grid-row">{columns}</div>;
    rows.push(row);
  }
  return <div className="background-grid">{rows}</div>;
}

ReactDOM.render(<BackgroundGrid />, document.getElementById("root"));
